import { useProjectStatusPoller } from '../../hooks/useProjectStatusPoller';

// ... (ProgressTracker component remains the same)

const ProjectCard: React.FC<ProjectCardProps> = ({ project }) => {
    const { updateProjectStatus } = useProjectStore();
    const navigate = useNavigate();
    const isProcessing = ['planning', 'searching', 'analyzing', 'synthesizing'].includes(project.status);
    useProjectStatusPoller(isProcessing ? project.id : undefined);

    const handleStartReview = async (e: React.MouseEvent) => {
        e.stopPropagation();
        // ...
    };

    const handleCardClick = () => {
        if (project.status === 'completed') {
            navigate(`/project/${project.id}`);
        }
    };

    const isReady = project.status === 'created';
    const isFailed = project.status === 'error' || project.status === 'error_no_papers_found';

    return (
        <Card 
            onClick={handleCardClick}
            className="flex flex-col h-full bg-white/60 backdrop-blur-lg border border-gray-200/50"
            sx={{ cursor: project.status === 'completed' ? 'pointer' : 'default', transition: 'all 0.2s ease-in-out', '&:hover': { transform: 'translateY(-4px)', boxShadow: '0 8px 25px rgba(0,0,0,0.1)' } }}
        >
            <CardContent className="flex-grow">
                <Box className="flex justify-between items-start mb-2">
                    <Typography variant="h6" component="div" className="font-bold text-slate-800 text-left">
                        {project.title}
                    </Typography>
                    <StatusChip status={project.status} />
                </Box>
                <Typography variant="body2" color="text.secondary" className="italic mb-2 text-left">
                    "{project.research_question}"
                </Typography>
            </CardContent>
            <CardActions className="bg-slate-50/50 p-4 flex justify-between items-center min-h-[80px]">
                {isReady && (
                    <Button size="small" variant="contained" onClick={handleStartReview} startIcon={<AutoAwesomeIcon />} className='bg-gradient-to-r from-blue-600 to-teal-500 hover:bg-blue-700 text-white'>
                        Start Review
                    </Button>
                )}
                {isProcessing && <ProgressTracker project={project} />}
                {project.status === 'completed' && (
                    <Button component={Link} to={`/project/${project.id}`} variant="contained" size="small" onClick={(e) => e.stopPropagation()} className="bg-gradient-to-r from-blue-600 to-blue-500 hover:bg-blue-700 text-white">
                        View Results
