const ProjectDetailsPage = () => {
    const { projectId } = useParams<{ projectId: string }>();
    const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
    const open = Boolean(anchorEl);

    const { data: project, isLoading, error } = useQuery({
        queryKey: ['project', projectId],
        queryFn: () => getProjectById(projectId!),
        enabled: !!projectId,
    });

    const handleClick = (event: React.MouseEvent<HTMLButtonElement>) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const exportToPdf = () => {
        const synthesizerPlan = project?.agent_plans.find(p => p.agent_type === 'synthesizer');
        const synthesisOutput = synthesizerPlan?.plan_steps[0]?.output.response || "No synthesis available.";

        const pdf = new jsPDF({
            orientation: 'p',
            unit: 'pt',
            format: 'a4'
        });

        const tree = unified().use(remarkParse).parse(synthesisOutput) as Root;

        const pageMargins = { left: 40, right: 40, top: 40, bottom: 40 };
        const pageHeight = pdf.internal.pageSize.getHeight();
        const usableWidth = pdf.internal.pageSize.getWidth() - pageMargins.left - pageMargins.right;
        let currentY = pageMargins.top;
        const lineSpacing = 1.4;
        const paragraphSpacing = 10;

        const checkAndAddPage = (elementHeight: number = 0) => {
            if (currentY + elementHeight > pageHeight - pageMargins.bottom) {
                pdf.addPage();
                currentY = pageMargins.top;
            }
        };

        const processNode = (node: Content) => {
            switch (node.type) {
                case 'heading': {
                    const text = node.children.map(getTextFromNode).join('');
                    const depth = node.depth;
                    const fontSize = { 1: 18, 2: 16, 3: 14, 4: 13, 5: 12, 6: 11 }[depth] || 12;
                    pdf.setFont('helvetica', 'bold');
                    pdf.setFontSize(fontSize);

                    const lines = pdf.splitTextToSize(text, usableWidth);
                    checkAndAddPage(lines.length * fontSize * lineSpacing);
                    pdf.text(lines, pageMargins.left, currentY);
                    currentY += lines.length * fontSize * lineSpacing;
                    break;
                }
                case 'paragraph': {
                    const text = node.children.map(getTextFromNode).join('');
                    pdf.setFont('times', 'normal');
                    pdf.setFontSize(12);

                    const lines = pdf.splitTextToSize(text, usableWidth);
                    checkAndAddPage(lines.length * 12 * lineSpacing);
                    pdf.text(lines, pageMargins.left, currentY);
                    currentY += lines.length * 12 * lineSpacing;
                    break;
                }
                case 'list': {
                    const isOrdered = node.ordered;
                    let counter = node.start || 1;
                    node.children.forEach(listItem => {
                        const prefix = isOrdered ? `${counter++}. ` : '• ';
                        const itemText = listItem.children.map(p => p.children.map(getTextFromNode).join('')).join('\n');
                        pdf.setFont('times', 'normal');
                        pdf.setFontSize(12);

                        const lines = pdf.splitTextToSize(itemText, usableWidth - 20); // Indent for bullet
                        checkAndAddPage(lines.length * 12 * lineSpacing);

                        pdf.text(prefix + lines[0], pageMargins.left, currentY);
                        if (lines.length > 1) {
                             pdf.text(lines.slice(1), pageMargins.left + 20, currentY + 12);
                        }
                        currentY += (lines.length * 12 * lineSpacing);
                    });
                    break;
                }
                 case 'thematicBreak': {
                    checkAndAddPage(10);
                    pdf.setDrawColor(200, 200, 200);
                    pdf.line(pageMargins.left, currentY, pdf.internal.pageSize.getWidth() - pageMargins.right, currentY);
                    currentY += 10;
                    break;
                }
                default:
                    break;
            }
            currentY += paragraphSpacing;
        };

        tree.children.forEach(processNode);

        pdf.save("literature-review.pdf");
        handleClose();
    };


    function getTextFromNode(node: PhrasingContent): string {
        if (node.type === 'text') return node.value;
        if ('children' in node) {
            return (node.children as PhrasingContent[]).map(getTextFromNode).join('');
        }
        return '';
    }

    function getHeadingLevel(depth: number): HeadingLevel {
        switch (depth) {
            case 1: return HeadingLevel.HEADING_1;
            case 2: return HeadingLevel.HEADING_2;
            case 3: return HeadingLevel.HEADING_3;
            case 4: return HeadingLevel.HEADING_4;
            case 5: return HeadingLevel.HEADING_5;
            case 6: return HeadingLevel.HEADING_6;
            default: return HeadingLevel.NORMAL;
        }
    }

    const markdownToDocxElements = (markdown: string): Paragraph[] => {
        const tree = unified().use(remarkParse).parse(markdown) as Root;
        const elements: Paragraph[] = [];

        tree.children.forEach(node => {
            if (node.type === 'paragraph') {
                const runs: TextRun[] = [];
                node.children.forEach(child => {
                    const text = getTextFromNode(child);
                    if (child.type === 'strong') {
                        runs.push(new TextRun({ text, bold: true }));
                    } else if (child.type === 'emphasis') {
                        runs.push(new TextRun({ text, italics: true }));
                    } else {
                        runs.push(new TextRun(text));
                    }
                });
                elements.push(new Paragraph({ children: runs }));
            } else if (node.type === 'heading') {
                const headingText = node.children.map(getTextFromNode).join('');
                elements.push(new Paragraph({
                    text: headingText,
                    heading: getHeadingLevel(node.depth),
                }));
            } else if (node.type === 'list') {
                 node.children.forEach(listItem => {
                    const text = listItem.children.map(p => p.children.map(getTextFromNode).join('')).join('\n');
                    elements.push(new Paragraph({
                        text: text,
                        bullet: { level: 0 }
                    }));
                });
            } else if (node.type === 'thematicBreak') {
                 elements.push(new Paragraph({ text: "---" })); // Simple representation of a thematic break
            }
        });

        return elements;
    };


    const exportToDocx = () => {
        const synthesizerPlan = project?.agent_plans.find(p => p.agent_type === 'synthesizer');
        const synthesisOutput = synthesizerPlan?.plan_steps[0]?.output.response || "No synthesis available.";

        const docElements = markdownToDocxElements(synthesisOutput);

        const doc = new Document({
            sections: [
                {
                    properties: {},
                    children: docElements,
                },
            ],
        });

        Packer.toBlob(doc).then(blob => {
            saveAs(blob, "literature-review.docx");
        });

        handleClose();
    };


    if (isLoading) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '80vh' }}>
                <CircularProgress />
            </Box>
        );
    }

    if (error) {
        return (
            <Box sx={{ px: { xs: 2, sm: 3 }, py: 4 }}>
                <Alert severity="error" variant="filled">Failed to load project details: {error.message}</Alert>
            </Box>
        );
    }

    if (!project) {
        return (
            <Box sx={{ px: { xs: 2, sm: 3 }, py: 4 }}>
                <Alert severity="warning" variant="filled">Project not found.</Alert>
            </Box>
        );
    }

    const synthesizerPlan = project.agent_plans.find(p => p.agent_type === 'synthesizer');
    const synthesisOutput = synthesizerPlan?.plan_steps[0]?.output.response || "No synthesis available.";

    return (
        <Box sx={{ px: { xs: 1, sm: 2, md: 16 }, py: { xs: 2, sm: 4 } }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mt: { xs: 6, sm: 5 } }}>
                <Button
                    component={Link}
                    to="/"
                    startIcon={<ArrowBackIcon />}
                    sx={{
                        fontWeight: 600,
                        fontSize: { xs: '0.875rem', sm: '1rem' }
                    }}
                >
                    Back to Projects
                </Button>
                <Button
                    id="export-button"
                    aria-controls={open ? 'export-menu' : undefined}
                    aria-haspopup="true"
                    aria-expanded={open ? 'true' : undefined}
                    onClick={handleClick}
                    variant="contained"
                >
                    Export
                </Button>
                <Menu
                    id="export-menu"
                    anchorEl={anchorEl}
                    open={open}
                    onClose={handleClose}
                    MenuListProps={{
                        'aria-labelledby': 'export-button',
                    }}
                >
                    <MenuItem onClick={exportToPdf}>Export as PDF</MenuItem>
                    <MenuItem onClick={exportToDocx}>Export as DOCX</MenuItem>
                </Menu>
            </Box>

            <Box className="bg-white/60 backdrop-blur-lg border border-gray-200/50 p-0 rounded-2xl" sx={{ mt: 2 }}>
                <Typography
                    variant="h4"
                    component="h1"
                    className="font-bold text-slate-800 mb-0"
                    sx={{
                        fontSize: { xs: '1.5rem', sm: '1.875rem', md: '2.125rem' },
                        px: { xs: 2, sm: 4 },
                        pt: { xs: 2, sm: 4 }
                    }}
                >
                    {project.title}
                </Typography>
                <Typography
                    variant="h6"
                    color="text.secondary"
                    className="italic mb-6"
                    sx={{
                        fontSize: { xs: '1rem', sm: '1.125rem', md: '1.25rem' },
                        p: { xs: 2, sm: 4 }
                    }}
                >
                    "{project.research_question}"
                </Typography>

                <Paper
                    elevation={0}
                    className="bg-slate-50/50 rounded-xl border border-slate-200/50"
                    sx={{
                        p: { xs: 2, sm: 4, md: 6 },
                        mb: { xs: 4, sm: 6, md: 8 },
                        mx: { xs: 1, sm: 2, md: 0 }
                    }}
                >
                    <Typography
